import { Directive , ElementRef , HostListener, ViewChild} from '@angular/core';

@Directive({
  selector: '[appRightscroll]'
})
export class RightscrollDirective {

  constructor( private el : ElementRef) { 
  }

  @HostListener('click')
  rightFunc(){
    let elm = this.el.nativeElement.parentElement.children[2];
    let item = elm.getElementsByClassName('item');
    // item[0].offSetWidth
    // let elm = this.el.nativeElement.offSetWidth;


    console.log(item);
    elm.scrollLeft  += item[0].clientWidth
    // item[0].scrollLeft += item[0].clientWidth
  }

}
