import { Component, HostListener } from '@angular/core';

@Component({
  selector: 'app-appshell',
  templateUrl: './appshell.component.html',
  styleUrls: ['./appshell.component.css']
})
export class AppshellComponent {

  scrollEffect: boolean = false;

  @HostListener("document:scroll")
  scrollEffectFunction(){
    if((document.body.scrollTop > 120) || (document.documentElement.scrollTop > 120)){
      this.scrollEffect = true;
    } else{
      this.scrollEffect = false;
    }
  }

}
<div class="">
    <div class="sticky header-z-index" [ngClass]="!scrollEffect ? 'bg-color-trans':'bg-color-white'">
        <div class="" [ngClass]="!scrollEffect ? '':'below-border'">
            <app-header></app-header>
        </div>
    </div>
    <main role="main">
        <app-homepage></app-homepage>
        <router-outlet></router-outlet>
    </main>
    <div class="bg-light">
        <app-footer></app-footer>
    </div>
</div>
