import { Directive, ElementRef , HostListener } from '@angular/core';

@Directive({
  selector: '[appOpacityscroll]'
})
export class OpacityscrollDirective {

  constructor( private el : ElementRef) { }



  @HostListener("document:scroll")
  scrollEffectFunction(){
    let elm = this.el.nativeElement;
    let elmParent = this.el.nativeElement.parentElement.clientHeight;
    let elmHeight = this.el.nativeElement.clientHeight;
    // if(0 <document.body.scrollTop < elmParent){
      elm.style.opacity = (((elmHeight - document.documentElement.scrollTop) / elmHeight) * 0.9) + 0.5;
      // console.log((((elmHeight - document.documentElement.scrollTop) / elmHeight) * 0.9 ) + 0.2)
    // } else{
      // elm.style.opacity = 1
    // }

  }

}
